import { Button, Card, CardContent, Typography } from "@mui/material"
import { useEffect, useState } from "react"

export default function DepartamentoList() {

  const [departamento, setDepartamento] = useState([])

  const loadDepartamento = async () => {
    const response = await fetch('http://localhost:5000/departamentos')
    const data = await response.json()
    setDepartamento(data)
  }

  useEffect(() => {
    loadDepartamento()
  }, [])

  return (
    <>
      <h1>Lista de Departamentos</h1>
      {
        departamento.map(departamento => (
          <Card style={{
            marginBottom: ".7rem",
            backgroundColor: '#1e272e'
          }}>
            <CardContent style={{
              display: "flex",
              justifyContent: "space-between"
            }}>
              <div>
                <Typography>{departamento.nombre_departamento}</Typography>
              </div>
              <div>
              <Button variant="contained" color="inherit">Actualizar</Button>
              <Button variant="contained" color="warning">Eliminar</Button>
              </div>
            </CardContent>
          </Card>
        ))
      }
    </>
  )
}
