import { Button, Card, CardContent, Grid, TextField, Typography } from "@mui/material"
import { useState, useEffect } from "react";


export default function DepartamentoForm() {

  const [departamento, setDepartamento] = useState({
    nombre_departamento: ''
  })
  const handleChange = (e) => {
    setDepartamento({...departamento, [e.target.name]: e.target.value})
  }
  const handleSubmit = e => {
    e.preventDefault();

    fetch('http://localhost:5000/departamentos', {
      method: 'POST',
      body: JSON.stringify(departamento)
    })

  }
  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={2}>
        <Card sx={{ mt: 10 }} style={{
          background: "#1e272e",
          padding: "1rem"
        }}>
          <Typography variant="5" textAlign="center" color="white"> Crear Departamento </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                variant="filled"
                placeholder="Nombre Departamento"
                label="Nombre del Departamento"
                sx={{ display: 'block', margin: '.5rem 0' }}
                name="nombre_departamento"
                onChange={handleChange}
                inputProps={{ style: { color: "white" } }}
                InputLabelProps={{ style: { color: "white" } }}></TextField>
              <Button variant="contained" color="primary" type="submit">
                Crear Departamento
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  )
}
