import * as React from 'react';
import { Button, Card, CardContent, Grid, TextField, Typography } from "@mui/material"
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

export default function InventarioForm() {

  const [inventario, setInventario] = useState({
    descripcion: "",
    cantidad: "",
    fk_iddepartamento: "",
    observaciones: ""
  })

  const [editing, setEditing] = useState(false)


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editing) {
      await fetch(`http://localhost:5000/inventario/${params.id}`, {
        method: 'PUT',
        body: JSON.stringify(inventario),
        headers: { "Content-Type": "application/json" }
      })

    } else {
      await fetch('http://localhost:5000/inventario', {
        method: 'POST',
        body: JSON.stringify(inventario),
        headers: { "Content-Type": "application/json" }
      })
    }
    navigate('/inventario')
  }

  const navigate = useNavigate()
  const params = useParams()

  const loadOneInventario = async (idinventario) => {
    const res = await fetch(`http://localhost:5000/inventario/${params.id}`)
    const data = await res.json()
    console.log(data)
    setDepartamento({ descripcion: data.descripcion, cantidad: data.cantidad, observaciones: data.observaciones})
    setEditing(true)
  }

  useEffect(() => {
    if (params.id) {
      loadOneDepartamento(params.id)
    }
  }, [params.id])

  return (
    <div>InventarioForm</div>
  )
}
