import { Button, Card, CardContent, Typography } from "@mui/material"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom";

export default function DepartamentoList() {

  const [departamento, setDepartamento] = useState([])

  const loadDepartamento = async () => {
    const response = await fetch('http://localhost:5000/departamentos')
    const data = await response.json()
    setDepartamento(data)
  }

  const handleDelete = async (id) => {
    const res = await fetch(`http://localhost:5000/departamentos/${id}`, {
      method: "DELETE"
    })
    const data = await res.json();
    navigate('/departamento')
    
    setDepartamento(
      departamento.filter(departamento => departamento.iddepartamento !== id)
    )
  }

  const navigate = useNavigate()

  useEffect(() => {
    loadDepartamento()
  }, [])

  return (
    <>
      <h1>Lista de Departamentos</h1>
      {
        departamento.map((departamento) => (
          <Card style={{
            marginBottom: ".7rem",
            backgroundColor: '#1e272e'
          }}
          key={departamento.iddepartamento}>
            <CardContent style={{
              display: "flex",
              justifyContent: "space-between"
            }}>
              <div style={{ color: "white" }}>
                <Typography >{departamento.nombre_departamento}</Typography>
              </div>
              <div>
                <Button variant="contained" color="inherit">Actualizar</Button>
                <Button variant="contained" color="warning"
                  onClick={() => handleDelete(departamento.iddepartamento)}
                  style={{ marginLeft: ".5rem" }}
                >Eliminar</Button>
              </div>
            </CardContent>
          </Card>
        ))
      }
    </>
  )
}
